import React, { useState, useEffect } from 'react';
import './BettingControls.css';

const BettingControls = ({ betSize, setBetSize, bankroll, gameOver, runningCount = 0 }) => {
  // Expanded bet sizes
  const betSizes = [10, 25, 50, 100, 200, 500];
  const [betToCountRatio, setBetToCountRatio] = useState(0);

  // Calculate bet to count ratio whenever bet size or running count changes
  useEffect(() => {
    if (runningCount <= 0) {
      // A high bet with zero/negative count is suspicious
      setBetToCountRatio(betSize / 50.0);
    } else {
      // Calculate normalized ratio
      setBetToCountRatio((betSize / 50.0) * (runningCount / Math.max(1, Math.abs(runningCount))));
    }
  }, [betSize, runningCount]);

  // Increment bet by 25
  const increaseBet = () => {
    if (betSize + 25 <= bankroll) {
      setBetSize(betSize + 25);
    }
  };

  // Decrement bet by 25
  const decreaseBet = () => {
    if (betSize - 25 >= 10) {
      setBetSize(betSize - 25);
    }
  };

  // Set bet to specific amount
  const setBet = (amount) => {
    if (amount <= bankroll) {
      setBetSize(amount);
    }
  };

  // Calculate the CSS class for the bet strategy indicator
  const getBetStrategyClass = () => {
    // Only show suspicious when running count is significant
    if (Math.abs(runningCount) < 2) return 'neutral';

    if (runningCount > 0) {
      // Positive count - should increase bet
      if (betSize > 100) return 'high';
      if (betSize > 50) return 'medium';
      return 'low';
    } else {
      // Negative count - should decrease bet
      if (betSize > 100) return 'high';
      if (betSize > 50) return 'medium';
      return 'low';
    }
  };

  // Get text description for bet strategy
  const getBetStrategyText = () => {
    const ratio = betToCountRatio.toFixed(1);

    if (Math.abs(runningCount) < 2) 
      return `Neutral (${ratio})`;

    if (runningCount > 0) {
      // Positive count
      if (betSize > 100) return `High correlation (${ratio})`;
      if (betSize > 50) return `Medium correlation (${ratio})`;
      return `Low correlation (${ratio})`;
    } else {
      // Negative count - inversely suspicious
      if (betSize > 100) return `Suspicious inverse (${ratio})`;
      if (betSize > 50) return `Medium inverse (${ratio})`;
      return `Low inverse (${ratio})`;
    }
  };

  return (
    <div className="betting-controls">
      <h3>Bet Size: ${betSize}</h3>

      <div className="bet-amount-controls">
        <button 
          onClick={decreaseBet} 
          disabled={betSize <= 10 || !gameOver}
          className="bet-button"
        >
          -
        </button>
        <span>${betSize}</span>
        <button 
          onClick={increaseBet} 
          disabled={betSize >= bankroll || !gameOver}
          className="bet-button"
        >
          +
        </button>
      </div>

      <div className="count-indicator">
        <div className="count-value">Count: {runningCount}</div>
        <div className="count-arrow-container">
          <div 
            className={`count-arrow ${runningCount > 0 ? 'positive' : runningCount < 0 ? 'negative' : ''}`}
            style={{ transform: `rotate(${Math.min(Math.max(runningCount * 15, -90), 90)}deg)` }}
          ></div>
        </div>
        <div className={`bet-strategy ${getBetStrategyClass()}`}>
          {getBetStrategyText()}
        </div>
      </div>

      <div className="bet-chips">
        {betSizes.map((amount) => (
          <button
            key={amount}
            onClick={() => setBet(amount)}
            disabled={amount > bankroll || !gameOver}
            className={`chip ${betSize === amount ? 'selected' : ''}`}
          >
            ${amount}
          </button>
        ))}
      </div>
    </div>
  );
};

export default BettingControls;
