import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import './CounterDetection.css';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const CounterDetection = ({ detection, runningCount }) => {
  // Prepare chart data if detection history exists
  const chartData = detection && detection.history ? {
    labels: detection.history.map(item => `Hand ${item.hand_number}`),
    datasets: [
      {
        label: 'Similarity Score',
        data: detection.history.map(item => item.similarity_score),
        borderColor: '#e63946',
        backgroundColor: 'rgba(230, 57, 70, 0.2)',
        tension: 0.4,
        borderWidth: 3,
        pointBackgroundColor: '#e63946',
        pointBorderColor: '#ffffff',
        pointRadius: 5,
        pointHoverRadius: 8,
        pointHoverBackgroundColor: '#e63946',
        pointHoverBorderColor: '#ffffff',
        yAxisID: 'y',
      },
      {
        label: 'Suspicion Threshold',
        data: detection.history.map(() => detection.similarity_threshold || 0.70),
        borderColor: '#adb5bd',
        backgroundColor: 'rgba(173, 181, 189, 0.1)',
        borderDash: [5, 5],
        borderWidth: 2,
        pointRadius: 0,
        yAxisID: 'y',
      },
      {
        label: 'Bet Size ($)',
        data: detection.history.map(item => item.bet_size / 10), // Scale down for better visibility
        borderColor: '#ffd700',
        backgroundColor: 'rgba(255, 215, 0, 0.2)',
        tension: 0.2,
        borderWidth: 2,
        pointRadius: 4,
        pointBackgroundColor: '#ffd700',
        borderDash: [3, 3],
        yAxisID: 'y1',
      },
      {
        label: 'Running Count',
        data: detection.history.map(item => item.running_count),
        borderColor: '#4cc9f0',
        backgroundColor: 'rgba(76, 201, 240, 0.2)',
        tension: 0.2,
        borderWidth: 2,
        pointRadius: 4,
        pointBackgroundColor: '#4cc9f0',
        yAxisID: 'y1',
      },
    ],
  } : null;

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    interaction: {
      mode: 'index',
      intersect: false,
    },
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: '#f8f9fa',
          font: {
            weight: 'bold'
          },
          usePointStyle: true,
        }
      },
      title: {
        display: true,
        text: 'Card Counting Detection Analysis',
        color: '#f8f9fa',
        font: {
          size: 16,
          weight: 'bold'
        },
        padding: {
          bottom: 20
        }
      },
      tooltip: {
        backgroundColor: 'rgba(26, 26, 46, 0.9)',
        borderColor: '#e63946',
        borderWidth: 1,
        titleColor: '#f8f9fa',
        bodyColor: '#f8f9fa',
        titleFont: {
          weight: 'bold'
        },
        callbacks: {
          label: function(context) {
            let label = context.dataset.label || '';
            if (label) {
              label += ': ';
            }
            if (context.parsed.y !== null) {
              if (label.includes('Bet Size')) {
                // Convert back to actual bet size
                label += '$' + (context.parsed.y * 10).toFixed(0);
              } else if (label.includes('Similarity')) {
                label += context.parsed.y.toFixed(3);
              } else {
                label += context.parsed.y.toFixed(1);
              }
            }
            return label;
          }
        }
      }
    },
    scales: {
      y: {
        type: 'linear',
        display: true,
        position: 'left',
        min: 0,
        max: 1,
        grid: {
          color: 'rgba(255, 255, 255, 0.05)'
        },
        ticks: {
          color: '#adb5bd'
        },
        title: {
          display: true,
          text: 'Similarity Score',
          color: '#f8f9fa',
          font: {
            weight: 'bold'
          }
        }
      },
      y1: {
        type: 'linear',
        display: true,
        position: 'right',
        min: -10,
        max: 50,
        grid: {
          drawOnChartArea: false,
          color: 'rgba(255, 255, 255, 0.05)'
        },
        ticks: {
          color: '#adb5bd'
        },
        title: {
          display: true,
          text: 'Bet Size & Count',
          color: '#f8f9fa',
          font: {
            weight: 'bold'
          }
        }
      },
      x: {
        grid: {
          color: 'rgba(255, 255, 255, 0.05)'
        },
        ticks: {
          color: '#adb5bd'
        }
      }
    },
    animation: {
      duration: 2000,
      easing: 'easeOutQuart'
    }
  };

  // Calculate status indicator class based on detection
  const getStatusClass = () => {
    if (!detection) return 'neutral';

    const { similarity_score } = detection;
    if (similarity_score > 0.85) return 'high';
    if (similarity_score > 0.7) return 'medium';
    return 'low';
  };

  // Get status text based on detection
  const getStatusText = () => {
    if (!detection) return 'No data';

    const { similarity_score } = detection;
    if (similarity_score > 0.85) return 'High Risk';
    if (similarity_score > 0.7) return 'Medium Risk';
    return 'Low Risk';
  };

  return (
    <div className="counter-detection">
      <h2>Card Counting Detection</h2>

      <div className="detection-content">
        <div className="detection-left">
          <div className="detection-stats">
            <div className="stat-item">
              <span className="stat-label">Running Count:</span>
              <span className="stat-value">{runningCount}</span>
            </div>

            {detection && (
              <>
                <div className="stat-item">
                  <span className="stat-label">Current Score:</span>
                  <span className="stat-value">
                    {detection.similarity_score.toFixed(3)}
                  </span>
                </div>

                <div className="stat-item">
                  <span className="stat-label">Status:</span>
                  <div className={`status-indicator ${getStatusClass()}`}>
                    {getStatusText()}
                  </div>
                </div>

                <div className="stat-item">
                  <span className="stat-label">Pattern Type:</span>
                  <span className="stat-value pattern-type">
                    {detection.counter_type || "Unknown"}
                  </span>
                </div>

                <div className="stat-item">
                  <span className="stat-label">Hands Analyzed:</span>
                  <span className="stat-value">
                    {detection.history ? detection.history.length : 0}
                  </span>
                </div>
              </>
            )}
          </div>

          <div className="detection-explanation">
            <h3>How it works:</h3>
            <p>
              This system detects card counting by analyzing your betting patterns 
              in relation to the running count. Card counters typically increase their 
              bets when the count is favorable (high positive count).
            </p>
            <p className="detector-note">
              The bet sizes you choose directly influence the detection algorithm.
              Varying bets in correlation with the count will trigger higher suspicion scores.
            </p>
          </div>
        </div>

        <div className="detection-chart">
          {chartData ? (
            <Line data={chartData} options={chartOptions} />
          ) : (
            <div className="no-data">
              Play more hands to generate detection data
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CounterDetection;
